package `0x03배열`import java.io.BufferedReaderimport java.io.InputStreamReaderfun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val n = br.readLine().toInt()    repeat(n) {        val (a, b) = br.readLine().split(" ")        println(check(a, b))    }}fun check(a: String, b: String): String {    val arrA = IntArray(26)    val arrB = IntArray(26)    for (i in a) {        arrA[i - 'a']++    }    for (i in b) {        arrB[i - 'a']++    }    return if (arrA.contentEquals(arrB)) {        "Possible"    } else {        "Impossible"    }}