package `0x03배열`import java.io.BufferedReaderimport java.io.InputStreamReaderfun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val (n, k) = br.readLine().split(" ").map { it.toInt() }    val students = Array(6) { IntArray(2) { 0 } }    repeat(n) {        val (s, y) = br.readLine().split(" ").map { it.toInt() }        students[y - 1][s]++    }    var ans = 0    for (i in students.indices) {        for (j in students[i].indices) {            ans += (students[i][j] + k - 1) / k        }    }    println(ans)}