package `0x03배열`import java.io.BufferedReaderimport java.io.InputStreamReaderfun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val arr = IntArray(10)    var num = 1    repeat(3) {        num *= br.readLine().toInt()    }    val strArr = num.toString().map { it.toString() }    for (i in strArr) {        arr[i.toInt()] += 1    }    arr.map { println(it) }}