package `0x03배열`import java.io.BufferedReaderimport java.io.InputStreamReaderimport kotlin.math.absfun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val first = br.readLine()    val second = br.readLine()    val arrA = IntArray(26)    val arrB = IntArray(26)    for (i in first) {        arrA[i - 'a']++    }    for (i in second) {        arrB[i - 'a']++    }    var ans = 0    for (i in 0..25) {        ans += abs(arrA[i] - arrB[i])    }    println(ans)}