package `0x03배열`import java.io.BufferedReaderimport java.io.InputStreamReaderimport kotlin.math.maxfun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val arr = IntArray(10)    val num = br.readLine()    var sixnine = 0    val strArr = num.toString().map { it.toString() }    for (i in strArr) {        if (i == "6" || i == "9") {            sixnine += 1        } else {            arr[i.toInt()] += 1        }    }    if (sixnine % 2 == 1) {        sixnine = sixnine / 2 + 1    } else sixnine /= 2    println(max(sixnine, arr.max()))}