package `0x03배열`import java.io.BufferedReaderimport java.io.InputStreamReaderfun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val n = br.readLine().toInt()    var ans = 0    val nums = br.readLine().split(" ").map { it.toInt() }.sorted()    val x = br.readLine().toInt()    var right = n - 1    var left = 0    while (left < right) {        val sum = nums[left] + nums[right]        if (sum == x) {            ans++            right--        } else if (sum < x) {            left++        } else {            right--        }    }        println(ans)}