package `0x04연결리스트`import java.io.BufferedReaderimport java.io.InputStreamReaderimport java.util.LinkedListfun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val (n, k) = br.readLine().split(" ").map { it.toInt() }    val lst = LinkedList<Int>()    for (i in 1..n) {        lst.add(i)    }    var ans = IntArray(0)    var cnt = 0    val cur = lst.listIterator()    while (lst.size > 1) {        cnt++        if (cur.hasNext()) {            cur.next()        } else {            while (cur.hasPrevious()) {                cur.previous()            }            cnt--        }        if (cnt == k) {            ans += cur.previous()            cur.remove()            cnt = 0        }    }    ans += lst.first()    println("<${ans.joinToString(", ")}>")}