package `0x04연결리스트`import java.io.BufferedReaderimport java.io.InputStreamReaderimport java.util.LinkedListfun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val lst = LinkedList<Char>()    br.readLine().forEach { lst.add(it) }    val n = br.readLine().toInt()    val cur = lst.listIterator()    while (cur.hasNext()) {        cur.next()    }    repeat(n) {        val command = br.readLine()        when (command[0]) {            'L' -> {                if (cur.hasPrevious()) {                    cur.previous()                }            }            'D' -> {                if (cur.hasNext()) {                    cur.next()                }            }            'B' -> {                if (cur.hasPrevious()) {                    cur.previous()                    cur.remove()                }            }            'P' -> {                cur.add(command[2])            }        }    }    print(lst.joinToString(""))}