package `0x04연결리스트`import java.io.BufferedReaderimport java.io.InputStreamReaderimport java.util.LinkedListfun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val n = br.readLine().toInt()    repeat(n) {        val cmd = br.readLine()        println(keyLogger(cmd))    }}fun keyLogger(cmd: String): String {    val newList = LinkedList<Char>()    val cur = newList.listIterator()    for (i in cmd) {        when (i) {            '<' -> {                if (cur.hasPrevious()) {                    cur.previous()                }            }            '>' -> {                if (cur.hasNext()) {                    cur.next()                }            }            '-' -> {                if (cur.hasPrevious()) {                    cur.previous()                    cur.remove()                }            }            else -> {                cur.add(i)            }        }    }    return newList.joinToString("")}