package `0x05스택`import java.io.BufferedReaderimport java.io.InputStreamReaderimport java.util.LinkedListimport java.util.Stackfun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val s = Stack<Int>()    val sb = StringBuilder()    var target = 1    val n = br.readLine().toInt()    repeat(n) {        val num = br.readLine().toInt()        while (target <= num) {            s.push(target++)            sb.append("+\n")        }        if (s.peek() == num) {            s.pop()            sb.append("-\n")        } else {            println("NO")            return        }    }    println(sb)}