package `0x05스택`import java.io.BufferedReaderimport java.io.InputStreamReaderimport java.util.LinkedListimport java.util.Stackfun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val n = br.readLine().toInt()    val s = Stack<Int>()    repeat(n) {        val cmd = br.readLine().split(" ")        val res = when (cmd[0]) {            "push" -> {                s.push(cmd[1].toInt())                null            }            "pop" -> {                if (!s.empty()) s.pop()                else -1            }            "top" -> {                if (!s.empty()) s.peek()                else -1            }            "size" -> s.size            else -> {                if (s.empty()) 1                else 0            }        }        if (res != null) println(res)    }}