package `0x06ÌÅê`import java.io.BufferedReaderimport java.io.InputStreamReaderimport java.util.LinkedListimport java.util.Queuefun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val n = br.readLine().toInt()    val q: Queue<Int> = LinkedList()    repeat(n) {        val cmd = br.readLine().split(" ")        val res = when (cmd[0]) {            "push" -> {                q.add(cmd[1].toInt())                null            }            "pop" -> {                if (!q.isEmpty()) q.poll()                else -1            }            "front" -> {                if (!q.isEmpty()) q.first()                else -1            }            "back" -> {                if (!q.isEmpty()) q.last()                else -1            }            "size" -> q.size            else -> {                if (q.isEmpty()) 1                else 0            }        }        if (res != null) println(res)    }}