package `0x06í`import java.io.BufferedReaderimport java.io.InputStreamReaderimport java.util.LinkedListimport java.util.Queuefun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val n = br.readLine().toInt()    println(card(n))}fun card(n: Int): Int {    val q: Queue<Int> = LinkedList()    for (i in 1..n) {        q.add(i)    }    while (q.size > 1) {        q.poll()        q.add(q.poll())    }    return q.first()}