package `0x07Îç±`import java.io.BufferedReaderimport java.io.InputStreamReaderimport java.util.StringTokenizerfun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val n = br.readLine().toInt()    val dq = ArrayDeque<Int>()    val sb = StringBuilder()    for (i in 1..n) {        val st = StringTokenizer(br.readLine(), " ")        when (st.nextToken()) {            "push_back" -> {                dq.addLast(st.nextToken().toInt())            }            "push_front" -> {                dq.addFirst(st.nextToken().toInt())            }            "pop_back" -> {                if (!dq.isEmpty()) {                    sb.append("${dq.removeLast()}\n")                } else sb.append("-1\n")            }            "pop_front" -> {                if (!dq.isEmpty()) {                    sb.append("${dq.removeFirst()}\n")                } else sb.append("-1\n")            }            "front" -> {                if (!dq.isEmpty()) {                    sb.append("${dq.first()}\n")                } else sb.append("-1\n")            }            "back" -> {                if (!dq.isEmpty()) {                    sb.append("${dq.last()}\n")                } else sb.append("-1\n")            }            "size" -> {                sb.append("${dq.size}\n")            }            else -> {                if (dq.isEmpty()) sb.append("1\n")                else sb.append("0\n")            }        }    }    println(sb)}