package basicimport java.io.BufferedReaderimport java.io.InputStreamReaderfun gcd(a: Int, b: Int): Int = if (b != 0) gcd(b, a % b) else afun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val t = br.readLine().toInt()    repeat(t) {        val nums = br.readLine()        val (A, B) = nums.split(" ").map { it.toInt() }        println((A * B) / gcd(A, B))    }}