package basicimport java.io.BufferedReaderimport java.io.InputStreamReaderfun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val t = br.readLine().toInt()    repeat(t) {        var ans = ""        val s = br.readLine()        val (n, str) = s.split(" ")        val res = str.map { char -> char.toString().repeat(n.toInt()) }.joinToString("")        println(res)    }}