package basicimport java.io.BufferedReaderimport java.io.InputStreamReaderfun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val dish = br.readLine()    var ans = 0    var last = '.'    for (i in dish.toCharArray()) {        if (i == last) {            ans += 5        } else {            ans += 10            last = i        }    }    print(ans)}