package bronzeimport java.io.BufferedReaderimport java.io.InputStreamReaderfun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val t = br.readLine().toInt()    repeat(t) {        val s = br.readLine()        var sum = 0.00        val parsed = s.split(" ")        for (i in parsed) {            when (i) {                "@" -> sum *= 3                "#" -> sum -= 7                "%" -> sum += 5                else -> sum = i.toDouble()            }        }        println("%.2f".format(sum))    }}