package bronzeimport java.util.*fun main(args: Array<String>) {    val input = Scanner(System.`in`)    val t = input.nextInt()    for (i in 1..t) {        val s = input.next()        println(ox(s))    }}fun ox(s: String): Int {    val length = s.length    var cnt = 1    var score = 0    for (i in 0..<length) {        if (s[i] == 'O') {            score += cnt            cnt += 1        } else {            cnt = 1        }    }    return score}