package week15import java.io.BufferedReaderimport java.io.InputStreamReaderfun main() {    val br = BufferedReader(InputStreamReader(System.`in`))    val N = br.readLine().toInt()    var ans = arrayOf<String>()    println(List(N) { "swimming" }.joinToString(" "))    System.out.flush()    val fail = br.readLine().split(" ")    for (i in fail) {        ans += if (i == "bowling") "soccer"        else "bowling"    }    println(ans.joinToString(" "))    System.out.flush()}